import React, { Component} from "react";
// import '../utilities.css'
// import "./Post.css";
import Image from 'react-bootstrap/Image';
import Button from 'react-bootstrap/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart } from '@fortawesome/free-solid-svg-icons'


class Post extends Component {
    constructor(props) {
        super(props);
        this.state = {
            liked: false,
          };
    }

    render() {
        if (this.state.liked) {
            return (
            <div>
                <h4>{this.props.info.title}</h4>
                <Image fluid="true" rounded="true" src={this.props.info.url} />
                <h5>{this.props.info.date}</h5>
                <Button onClick={this.setState({liked: true})} variant="outline-primary"><FontAwesomeIcon icon={faHeart} /></Button>
                <p>{this.props.info.explanation}</p>
            </div>
            )
        } else {
            return (
            <div>
                <h4>{this.props.info.title}</h4>
                <Image fluid="true" rounded="true" src={this.props.info.url} />
                <h5>{this.props.info.date}</h5>
                <Button onClick={this.setState({liked: true})} variant="outline-primary"><FontAwesomeIcon icon={faHeart} /></Button>
                <p>{this.props.info.explanation}</p>
            </div> )
        }
    }
};

export default Post;
