import React, {Component} from "react"
import Container from 'react-bootstrap/Container';
import Post from './Post.js';
import './App.css'


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch("https://api.nasa.gov/planetary/apod?api_key=5lyfFNopm9g1iZ8prQ6avveLCX2eubpBBlLkqYR7")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <>
        <Container fluid>
          <Container className="p-5 mb-4 bg-light rounded-3">
          <h1 className="header text-center lg">SPACESTAGRAM</h1>
          <h5 className="text-center text-secondary mb-4">From the NASA's Astronomy Picture of the Day</h5>
          <Post info={items} />
          </Container>
        </Container>
        </>
        )
    }

}
}

export default App;
