import React, { Component} from "react";
// import '../utilities.css'
// import "./Post.css";
import Image from 'react-bootstrap/Image';
import Button from 'react-bootstrap/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart } from '@fortawesome/free-solid-svg-icons'


class Post extends Component {
    constructor(props) {
        super(props);
    }

    render() {

        return (

            <div>
                <h4>{this.props.info.title}</h4>
                <Image fluid="true" rounded="true" src={this.props.info.url} />
                <h5>{this.props.info.date}</h5>
                <Button className="btn-floating btn-outline-secondary waves-effect px-3"><FontAwesomeIcon icon={faHeart} /></Button>
                <button type="button" class="btn btn-default btn-lg">
                    <span class="glyphicon glyphicon-star" aria-hidden="true"></span> Star
                </button>
                <p>{this.props.info.explanation}</p>
            </div>
        )
    }
};

export default Post;
